import React, { useState, useEffect } from "react";
import MapComponent from "../components/LeafletMap";
import "../styles/home.css";

export default function Home() {
  const [userLocation, setUserLocation] = useState(null);
  const [garages, setGarages] = useState([]); 

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (pos) => {
          const { latitude, longitude } = pos.coords;
          const userCoords = [latitude, longitude];
          setUserLocation(userCoords);

          // Fetch garages from Google Places API
        //   const nearbyGarages = await fetchNearbyGarages(latitude, longitude);
        //   setGarages(
        //     nearbyGarages.map((place) => ({
        //       position: [place.geometry.location.lat, place.geometry.location.lng],
        //       popup: place.name,
        //     }))
        //   );
        },
        (error) => console.error("Error getting location:", error),
        { enableHighAccuracy: true }
      );
    } else {
      console.error("Geolocation is not supported by this browser.");
    }
  }, []);

//   const fetchNearbyGarages = async (lat, lng) => {
//     const fakeGarages = [
//       { position: [lat + 0.002, lng + 0.002], popup: "Garage A" },
//       { position: [lat - 0.002, lng - 0.002], popup: "Garage B" },
//       { position: [lat + 0.001, lng - 0.002], popup: "Garage C" },
//     ];
//     setGarages(fakeGarages);
//   };

  return (
    <div className="container">
      <div className="map-wrapper">
        {userLocation ? (
          <MapComponent center={userLocation} userLocation={userLocation} markers={garages} />
        ) : (
          <p>Loading location...</p>
        )}
      </div>
    </div>
  );
}
const fetchNearbyGarages = async (lat, lng) => {
    const query = `
      [out:json];
      (
        node["amenity"="car_repair"](around:1000, ${lat}, ${lng});
      );
      out body;
    `;
    const url = `https://overpass-api.de/api/interpreter?data=${encodeURIComponent(query)}`;
  
    try {
      const response = await fetch(url);
      const data = await response.json();
      return data.elements.map((place) => ({
        position: [place.lat, place.lon],
        popup: place.tags.name || "Unnamed Garage",
      }));
    } catch (error) {
      console.error("Error fetching garages:", error);
      return [];
    }
  };
