import React from "react";
import PropTypes from "prop-types";
import { MDBCard, MDBCardBody, MDBCardImage, MDBCardText, MDBCardTitle, MDBBtn, MDBIcon } from "mdb-react-ui-kit";
import "../styles/sidebar.css";

const Sidebar = ({ isOpen, toggleSidebar, selectedGarage, closeGarage }) => {
  return (
    <div className={`sidebar ${isOpen ? "open" : "closed"}`}>
      {/* Toggle Button */}
      <MDBBtn floating className="toggle-btn" onClick={toggleSidebar}>
        <MDBIcon fas icon={isOpen ? "angle-left" : "angle-right"} />
      </MDBBtn>

      {/* Sidebar Content */}
      {isOpen && (
        <>
          <h4 className="sidebar-title">Garage Details</h4>
          {selectedGarage ? (
            <MDBCard className="garage-card">
              <MDBCardImage src={selectedGarage.image} position="top" alt={selectedGarage.name} />
              <MDBCardBody>
                <MDBCardTitle>{selectedGarage.name}</MDBCardTitle>
                <MDBCardText>üìç {selectedGarage.address}</MDBCardText>
                <MDBCardText>‚≠ê {selectedGarage.rating} / 5</MDBCardText>
                <MDBCardText>üìè {selectedGarage.distance} away</MDBCardText>
                <MDBCardText>üïí {selectedGarage.openingHours}</MDBCardText>
                <MDBBtn color="danger" onClick={closeGarage}>Close</MDBBtn>
              </MDBCardBody>
            </MDBCard>
          ) : (
            <p className="info-text">Click on a garage marker to view details</p>
          )}
        </>
      )}
    </div>
  );
};

Sidebar.propTypes = {
  isOpen: PropTypes.bool.isRequired,
  toggleSidebar: PropTypes.func.isRequired,
  selectedGarage: PropTypes.object,
  closeGarage: PropTypes.func.isRequired,
};

export default Sidebar;
