import React, { useState } from "react";
import PropTypes from "prop-types";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import "../styles/leafletCard.css"; // CSS for the custom card popup

// ‚úÖ Marker Icons
const redIcon = new L.Icon({
  iconUrl: "https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-red.png",
  shadowUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.3.1/images/marker-shadow.png",
  iconSize: [25, 41],
  iconAnchor: [12, 41],
});

const blueIcon = new L.Icon({
  iconUrl: "https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-blue.png",
  shadowUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.3.1/images/marker-shadow.png",
  iconSize: [25, 41],
  iconAnchor: [12, 41],
});

const MapComponent = ({
  center = [51.505, -0.09],
  userLocation,
  zoom = 13,
  tileLayerURL = "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png",
  markers = [],
  style = { height: "600px", width: "100%" },
}) => {
  const [selectedGarage, setSelectedGarage] = useState(null);

  return (
    <MapContainer center={center} zoom={zoom} style={style}>
      <TileLayer url={tileLayerURL} />

      {/* ‚úÖ User Location (Red Marker) */}
      {userLocation && (
        <Marker position={userLocation} icon={redIcon}>
          <Popup>You are here!</Popup>
        </Marker>
      )}

      {/* ‚úÖ Garage Locations (Blue Markers) */}
      {markers.map((garage) => (
        <Marker
          key={garage.id}
          position={garage.position}
          icon={blueIcon}
          eventHandlers={{
            click: () => {
              setSelectedGarage(garage);
            },
          }}
        />
      ))}

      {/* ‚úÖ Google Maps Style Card Popup */}
      {selectedGarage && (
        <div className="popup-card">
          <img src={selectedGarage.photo} alt={selectedGarage.name} />
          <h3>{selectedGarage.name}</h3>
          <p>{selectedGarage.address}</p>
          <p>‚≠ê {selectedGarage.rating}</p>
          <p>üìç {selectedGarage.distance} away</p>
          <button onClick={() => setSelectedGarage(null)}>Close</button>
        </div>
      )}
    </MapContainer>
  );
};

MapComponent.propTypes = {
  center: PropTypes.arrayOf(PropTypes.number),
  userLocation: PropTypes.arrayOf(PropTypes.number),
  zoom: PropTypes.number,
  tileLayerURL: PropTypes.string,
  markers: PropTypes.arrayOf(
    PropTypes.shape({
      position: PropTypes.arrayOf(PropTypes.number).isRequired,
      name: PropTypes.string,
      address: PropTypes.string,
      rating: PropTypes.string,
      distance: PropTypes.string,
      photo: PropTypes.string,
    })
  ),
  style: PropTypes.object,
};

export default MapComponent;
