import React, { useState, useEffect } from "react";
import MapComponent from "../components/LeafletMap";
import "../styles/home.css";

export default function Home() {
  const [userLocation, setUserLocation] = useState(null);
  const [garages, setGarages] = useState([]);
  const [selectedGarage, setSelectedGarage] = useState(null); // Store selected garage

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (pos) => {
          const { latitude, longitude } = pos.coords;
          const userCoords = [latitude, longitude];
          setUserLocation(userCoords);

          // Fetch garages from API (or use dummy data)
          const nearbyGarages = await fetchNearbyGarages(latitude, longitude);
          setGarages(nearbyGarages);
        },
        (error) => console.error("Error getting location:", error),
        { enableHighAccuracy: true }
      );
    } else {
      console.error("Geolocation is not supported by this browser.");
    }
  }, []);

  return (
    <div className="container">
      <div className="sidebar">
        <h2>Garage Details</h2>
        {selectedGarage ? (
          <div className="garage-details">
            <img src={selectedGarage.image} alt={selectedGarage.name} className="garage-image" />
            <h3>{selectedGarage.name}</h3>
            <p>üìç {selectedGarage.address}</p>
            <p>‚≠ê {selectedGarage.rating} / 5</p>
            <p>üìè {selectedGarage.distance} away</p>
            <p>üïí {selectedGarage.openingHours}</p>
          </div>
        ) : (
          <p>Click on a garage marker to view details</p>
        )}
      </div>
      <div className="map-wrapper">
        {userLocation && (
          <MapComponent
            center={userLocation}
            userLocation={userLocation}
            markers={garages}
            setSelectedGarage={setSelectedGarage} // Pass function to MapComponent
          />
        )}
      </div>
    </div>
  );
}

// ‚úÖ Fetch Garage Details (Dummy Data)
const fetchNearbyGarages = async (lat, lng) => {
  const dummyGarages = [
    {
      id: 1,
      position: [lat + 0.002, lng + 0.002],
      name: "Elite Auto Repair",
      address: "123 Main Street, Downtown",
      rating: 4.5,
      distance: (Math.random() * 5 + 1).toFixed(2) + " km",
      openingHours: "9:00 AM - 6:00 PM",
      image: "https://source.unsplash.com/200x150/?garage,car-repair",
    },
    {
      id: 2,
      position: [lat - 0.002, lng - 0.002],
      name: "Speedy Garage Services",
      address: "456 Elm Street, Midtown",
      rating: 4.2,
      distance: (Math.random() * 5 + 1).toFixed(2) + " km",
      openingHours: "8:00 AM - 7:00 PM",
      image: "https://source.unsplash.com/200x150/?mechanic,workshop",
    },
  ];

  return dummyGarages;
};
