import React from "react";
import PropTypes from "prop-types";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

// Fix for default Leaflet marker icon not loading in React
import markerIconPng from "leaflet/dist/images/marker-icon.png";
import markerShadowPng from "leaflet/dist/images/marker-shadow.png";

const defaultIcon = new L.Icon({
  iconUrl: markerIconPng,
  shadowUrl: markerShadowPng,
  iconSize: [25, 41],
  iconAnchor: [12, 41],
});

const MapComponent = ({
  center, // Default center (London)
  zoom = 13, // Default zoom level
  tileLayerURL = "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png",
  markers = [], // Default to an empty array to prevent forced rendering
  style = { height: "600px", width: "100%" }, // Default map size
}) => {
  return (
    <MapContainer center={center} zoom={zoom} style={style}>
      {/* Dynamic Tile Layer */}
      <TileLayer  url={tileLayerURL}  />

      {/* Render Markers if they exist */}
      {markers.length > 0 &&
        markers.map((marker, index) => (
          <Marker key={index} position={marker.position} icon={marker.icon || defaultIcon}>
            {marker.popup && <Popup>{marker.popup}</Popup>}
          </Marker>
        ))}
    </MapContainer>
  );
};

// Add PropTypes for Type Checking
MapComponent.propTypes = {
  center: PropTypes.arrayOf(PropTypes.number), // Expected: [lat, lng]
  zoom: PropTypes.number,
  tileLayerURL: PropTypes.string,
  markers: PropTypes.arrayOf(
    PropTypes.shape({
      position: PropTypes.arrayOf(PropTypes.number).isRequired, // Expected: [lat, lng]
      popup: PropTypes.string, // Optional popup text
      icon: PropTypes.object, // Optional Leaflet icon instance
    })
  ),
  style: PropTypes.object,
};

export default MapComponent;
