import React, { useState, useEffect } from "react";
import MapComponent from "../components/LeafletMap";
import { MDBCard, MDBCardBody, MDBCardImage, MDBCardText, MDBCardTitle, MDBBtn, MDBIcon } from "mdb-react-ui-kit";
import "../styles/home.css";

export default function Home() {
  const [userLocation, setUserLocation] = useState(null);
  const [garages, setGarages] = useState([]);
  const [selectedGarage, setSelectedGarage] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(true); // Sidebar open by default

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (pos) => {
          const { latitude, longitude } = pos.coords;
          const userCoords = [latitude, longitude];
          setUserLocation(userCoords);

          const nearbyGarages = await fetchNearbyGarages(latitude, longitude);
          setGarages(nearbyGarages);
        },
        (error) => console.error("Error getting location:", error),
        { enableHighAccuracy: true }
      );
    } else {
      console.error("Geolocation is not supported by this browser.");
    }
  }, []);

  return (
    <div className="map-container">
      {/* Floating Sidebar */}
      <div className={`sidebar ${sidebarOpen ? "open" : "closed"}`}>
        {/* Toggle Button */}
        <MDBBtn floating className="toggle-btn" onClick={() => setSidebarOpen(!sidebarOpen)}>
          <MDBIcon fas icon={sidebarOpen ? "angle-left" : "angle-right"} />
        </MDBBtn>

        {/* Sidebar Content */}
        {sidebarOpen && (
          <>
            <h4 className="sidebar-title">Garage Details</h4>
            {selectedGarage ? (
              <MDBCard className="garage-card">
                <MDBCardImage src={selectedGarage.image} position="top" alt={selectedGarage.name} />
                <MDBCardBody>
                  <MDBCardTitle>{selectedGarage.name}</MDBCardTitle>
                  <MDBCardText>üìç {selectedGarage.address}</MDBCardText>
                  <MDBCardText>‚≠ê {selectedGarage.rating} / 5</MDBCardText>
                  <MDBCardText>üìè {selectedGarage.distance} away</MDBCardText>
                  <MDBCardText>üïí {selectedGarage.openingHours}</MDBCardText>
                  <MDBBtn color="danger" onClick={() => setSelectedGarage(null)}>Close</MDBBtn>
                </MDBCardBody>
              </MDBCard>
            ) : (
              <p className="info-text">Click on a garage marker to view details</p>
            )}
          </>
        )}
      </div>

      {/* Map */}
      <div className="map-wrapper">
        {userLocation && (
          <MapComponent center={userLocation} userLocation={userLocation} markers={garages} setSelectedGarage={setSelectedGarage} />
        )}
      </div>
    </div>
  );
}

// Dummy Garage Data
const fetchNearbyGarages = async (lat, lng) => {
  return [
    {
      id: 1,
      position: [lat + 0.002, lng + 0.002],
      name: "Elite Auto Repair",
      address: "123 Main Street, Downtown",
      rating: 4.5,
      distance: (Math.random() * 5 + 1).toFixed(2) + " km",
      openingHours: "9:00 AM - 6:00 PM",
      image: "https://source.unsplash.com/300x200/?garage,car-repair",
    },
    {
      id: 2,
      position: [lat - 0.002, lng - 0.002],
      name: "Speedy Garage Services",
      address: "456 Elm Street, Midtown",
      rating: 4.2,
      distance: (Math.random() * 5 + 1).toFixed(2) + " km",
      openingHours: "8:00 AM - 7:00 PM",
      image: "https://source.unsplash.com/300x200/?mechanic,workshop",
    },
  ];
};
