import React from "react";
import PropTypes from "prop-types";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

// ‚úÖ Define Marker Icons
const redIcon = new L.Icon({
  iconUrl: "https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-red.png",
  shadowUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.3.1/images/marker-shadow.png",
  iconSize: [25, 41],
  iconAnchor: [12, 41],
});

const blueIcon = new L.Icon({
  iconUrl: "https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-blue.png",
  shadowUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.3.1/images/marker-shadow.png",
  iconSize: [25, 41],
  iconAnchor: [12, 41],
});

const MapComponent = ({
  center = [51.505, -0.09],
  userLocation,
  zoom = 13,
  tileLayerURL = "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png",
  markers = [],
  style = { height: "600px", width: "100%" },
}) => {
  return (
    <MapContainer center={center} zoom={zoom} style={style}>
      <TileLayer url={tileLayerURL} />

      {/* ‚úÖ User's Current Location (Red Marker) */}
      {userLocation && (
        <Marker position={userLocation} icon={redIcon}>
          <Popup>You are here!</Popup>
        </Marker>
      )}

      {/* ‚úÖ Garage Locations (Blue Markers) with Popups */}
      {markers.map((garage) => (
        <Marker key={garage.id} position={garage.position} icon={blueIcon}>
          <Popup>
            <div className="popup-content">
              <img src={garage.image} alt={garage.name} className="popup-image" />
              <h3>{garage.name}</h3>
              <p>üè† {garage.address}</p>
              <p>‚≠ê {garage.rating} / 5</p>
              <p>üìç {garage.distance} away</p>
              <p>üïí Open: {garage.openingHours}</p>
            </div>
          </Popup>
        </Marker>
      ))}
    </MapContainer>
  );
};

// ‚úÖ PropTypes Validation
MapComponent.propTypes = {
  center: PropTypes.arrayOf(PropTypes.number),
  userLocation: PropTypes.arrayOf(PropTypes.number),
  zoom: PropTypes.number,
  tileLayerURL: PropTypes.string,
  markers: PropTypes.arrayOf(
    PropTypes.shape({
      position: PropTypes.arrayOf(PropTypes.number).isRequired,
      name: PropTypes.string,
      address: PropTypes.string,
      rating: PropTypes.number,
      distance: PropTypes.string,
      openingHours: PropTypes.string,
      image: PropTypes.string,
    })
  ),
  style: PropTypes.object,
};

export default MapComponent;
