import React, { useState } from "react";
import PropTypes from "prop-types";
import { MapContainer, TileLayer, Marker } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

// Define Marker Icons
const redIcon = new L.Icon({
  iconUrl: "https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-red.png",
  shadowUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.3.1/images/marker-shadow.png",
  iconSize: [25, 41],
  iconAnchor: [12, 41],
});

const blueIcon = new L.Icon({
  iconUrl: "https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-blue.png",
  shadowUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.3.1/images/marker-shadow.png",
  iconSize: [25, 41],
  iconAnchor: [12, 41],
});

const highlightedIcon = new L.Icon({
  iconUrl: "https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-yellow.png",
  shadowUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.3.1/images/marker-shadow.png",
  iconSize: [30, 50], // Bigger when selected
  iconAnchor: [15, 50],
});

const MapComponent = ({ center, userLocation, zoom = 13, markers = [], setSelectedGarage, style = { height: "100vh", width: "100%" } }) => {
  const [selectedMarkerId, setSelectedMarkerId] = useState(null);

  return (
    <MapContainer center={center} zoom={zoom} style={style}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

      {/* User's Location */}
      {userLocation && <Marker position={userLocation} icon={redIcon} />}

      {/* Garage Markers */}
      {markers.map((garage) => (
        <Marker
          key={garage.id}
          position={garage.position}
          icon={selectedMarkerId === garage.id ? highlightedIcon : blueIcon}
          eventHandlers={{
            click: () => {
              setSelectedMarkerId(garage.id);
              setSelectedGarage(garage);
            },
          }}
        />
      ))}
    </MapContainer>
  );
};

// PropTypes
MapComponent.propTypes = {
  center: PropTypes.arrayOf(PropTypes.number),
  userLocation: PropTypes.arrayOf(PropTypes.number),
  zoom: PropTypes.number,
  markers: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.number.isRequired,
      position: PropTypes.arrayOf(PropTypes.number).isRequired,
      name: PropTypes.string,
      address: PropTypes.string,
      rating: PropTypes.number,
      distance: PropTypes.string,
      openingHours: PropTypes.string,
      image: PropTypes.string,
    })
  ),
  setSelectedGarage: PropTypes.func.isRequired,
  style: PropTypes.object,
};

export default MapComponent;
